    }
    function handleSub(e){
        e.preventDefault();
        if(!onSubmit) return;
        const formValues = {
            title:value
        }
        onSubmit(formValues)
    }
    return(
        <form className="mt-3 mb-3" onSubmit={handleSub}>
            <input style={{width:'100%'}} type="text" value={value} onChange={handleChange}/>
        </form>
    )
}
